#!/usr/bin/env bash
set -euo pipefail
ROOT="${1:-.}"
echo "Applying files into: $ROOT"
mkdir -p "$ROOT/templates/pages" "$ROOT/templates" "$ROOT/events/templates/events" "$ROOT/events/static/events" "$ROOT/static/docs"
cat >"$ROOT/templates/pages/home.html" <<'EOF'
{% extends "base.html" %}
{% load static i18n %}

{% block title %}{% trans "Онлайн научные мероприятия — Главная" %}{% endblock %}

{% block extra_head %}<link rel="stylesheet" href="{% static 'events/cards.css' %}">{% endblock %}
{% block content %}
<section class="hero">
  <h1 class="hero-title">{% trans "Онлайн научные мероприятия для школьников, учащихся СПО, студентов, аспирантов, учёных" %}</h1>
  <p class="hero-sub">{% trans "Наградные документы высылаются в течение 5 дней с даты регистрации" %}</p>

  <form class="search-form" action="" onsubmit="return false;">
    <input id="globalSearch" type="search" placeholder="{% trans 'Начните вводить название мероприятия…' %}" aria-label="{% trans 'Поиск по заголовку мероприятия' %}">
    <button type="button" class="cta" onclick="window.performGlobalSearch && window.performGlobalSearch()">{% trans "Искать" %}</button>
  </form>

  <div class="tiles">
    <a class="tile" href="/olympiads/">
      <span class="tile-title">{% trans "Олимпиады" %}</span>
    </a>
    <a class="tile" href="/contests/">
      <span class="tile-title">{% trans "Конкурсы статей, ВКР, научных работ" %}</span>
    </a>
    <a class="tile" href="/conferences/">
      <span class="tile-title">{% trans "Конференции с публикацией в РИНЦ сборниках" %}</span>
    </a>
    <a class="tile" href="https://vsem.site/" target="_blank" rel="noopener">
      <span class="tile-title">{% trans "Монографии" %}</span>
    </a>
    <a class="tile" href="https://vsem.site/" target="_blank" rel="noopener">
      <span class="tile-title">{% trans "Наши журналы" %}</span>
    </a>
  </div>
</section>

<section class="section">
  <div class="section-head">
    <h2 class="section-title">{% trans "Актуальные мероприятия" %}</h2>
  </div>

  <div id="actualEvents" class="cards">
    {% for ev in featured_events %}
      {% include 'events/card.html' with ev=ev %}
    {% empty %}
      <div class="empty">{% trans "Пока нет выбранных мероприятий. Добавьте их в админке." %}</div>
    {% endfor %}
  </div>
</section>
{% endblock %}

EOF
cat >"$ROOT/templates/pages/about.html" <<'EOF'
{% extends "base.html" %}
{% block title %}О нас
<section id="docs" class="legal-docs" style="margin-top: 24px;">
  <h2>Документы</h2>
  <p>Все юридически значимые документы размещены на этой странице и входят в состав репозитория.</p>
  <ul>
    <li><a href="{% static 'docs/oferta.docx' %}" download>Публичная оферта (DOCX)</a></li>
    <li><a href="{% static 'docs/privacy_policy.docx' %}" download>Политика конфиденциальности (DOCX)</a></li>
  </ul>
</section>
{% endblock %}

{% block content %}
<section class="about container">
  <h1>О нас</h1>
  {% if about %}
    <div class="about-content">
      <div class="lang-ru"><h3>RU</h3><p style="white-space:pre-wrap">{{ about.content_ru }}</p></div>
      <div class="lang-en"><h3>EN</h3><p style="white-space:pre-wrap">{{ about.content_en }}</p></div>
    </div>
  {% else %}
    <p>Раздел пока пуст. Заполните текст в админке.</p>
  {% endif %}
</section>

<section id="docs" class="legal-docs" style="margin-top: 24px;">
  <h2>Документы</h2>
  <p>Все юридически значимые документы размещены на этой странице и входят в состав репозитория.</p>
  <ul>
    <li><a href="{% static 'docs/oferta.docx' %}" download>Публичная оферта (DOCX)</a></li>
    <li><a href="{% static 'docs/privacy_policy.docx' %}" download>Политика конфиденциальности (DOCX)</a></li>
  </ul>
</section>
{% endblock %}


EOF
cat >"$ROOT/templates/base.html" <<'EOF'
{% load static i18n %}
<!DOCTYPE html>
<html lang="{{ LANGUAGE_CODE|default:'ru' }}">
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>{% block title %}Олимпиады, Конкурсы, Конференции — Научные мероприятия высокого уровня{% endblock %}</title>
  <link rel="icon" type="image/svg+xml" href="{% static 'img/favicon.svg' %}">
  <link rel="stylesheet" href="{% static 'css/main.css' %}">
  {% block extra_head %}{% endblock %}
</head>
<body class="theme-peacock">
  <!-- Global watermark -->
  <div class="page-watermark" aria-hidden="true"></div>

  <!-- Top utility strip with language switch -->
  <div class="top-strip">
    <form action="{% url 'set_language' %}" method="post">{% csrf_token %}
      <input name="next" type="hidden" value="{{ request.path }}">
      <select name="language" class="lang-select" onchange="this.form.submit()">
        <option value="ru" {% if LANGUAGE_CODE == 'ru' %}selected{% endif %}>RU</option>
        <option value="en" {% if LANGUAGE_CODE == 'en' %}selected{% endif %}>EN</option>
      </select>
    </form>
  </div>

  <!-- Header / Navigation -->
  <header class="site-header">
    <div class="brand">
      <a href="/" class="brand-title">
        Олимпиады, Конкурсы, Конференции<br><span class="brand-sub">Научные мероприятия высокого уровня</span>
      </a>
    </div>
    <nav class="main-nav">
      <a class="nav-btn" href="/">Главная</a>
      <a class="nav-btn" href="/olympiads/">Олимпиады</a>
      <a class="nav-btn" href="/contests/">Конкурсы статей, ВКР, научных работ</a>
      <a class="nav-btn" href="/conferences/">Конференции с публикацией в РИНЦ сборниках</a>
      <a class="nav-btn" href="https://vsem.site/" target="_blank" rel="noopener">Монографии</a>
      <a class="nav-btn" href="https://vsem.site/" target="_blank" rel="noopener">Наши журналы</a>
      <a class="nav-btn" href="/about/">О нас</a>
    </nav>
  </header>

  <!-- Content -->
  <main id="content">
    {% block content %}{% endblock %}
  </main>

  <!-- Footer -->
  \1
<div class="legal-links" style="margin-top:12px;"><a href="/about/#docs">Оферта и Политика конфиденциальности</a></div>
    <div class="footer-links">
      {% if site_policy_docx_url %}
        <a class="doc-link" href="{{ site_policy_docx_url }}">Политика конфиденциальности (DOCX)</a>
      {% endif %}
      {% if site_terms_docx_url %}
        <a class="doc-link" href="{{ site_terms_docx_url }}">Пользовательское соглашение (DOCX)</a>
      {% endif %}
    </div>
    <div class="copyright">© {{ now|date:"Y" }} Все права защищены</div>
  </footer>

  <script src="{% static 'js/search.js' %}"></script>
  {% block extra_js %}{% endblock %}
</body>
</html>

EOF
cat >"$ROOT/events/templates/events/card.html" <<'EOF'
{% comment %}Единая карточка мероприятия{% endcomment %}
<div class="card">
  <h3 class="card-title"><a href="{{ ev.get_absolute_url }}">{{ ev.title }}</a></h3>
  {% if ev.short_description %}
    <p class="card-desc">{{ ev.short_description|truncatechars:180 }}</p>
  {% elif ev.description %}
    <p class="card-desc">{{ ev.description|truncatechars:180 }}</p>
  {% endif %}
  <div class="card-actions">
    {% if ev.info_file %}<a class="btn" href="{{ ev.info_file.url }}" download>Информационное письмо</a>{% endif %}
    <a class="btn primary" href="{{ ev.get_register_url }}">Регистрация</a>
  </div>
</div>

EOF
cat >"$ROOT/events/static/events/cards.css" <<'EOF'

/* Grid for event cards: 3 per row */
.cards-grid{display:grid;grid-template-columns:repeat(3,minmax(0,1fr));gap:24px}
@media(max-width:1100px){.cards-grid{grid-template-columns:repeat(2,minmax(0,1fr))}}
@media(max-width:700px){.cards-grid{grid-template-columns:1fr}}

.card{background:#fff;border:1px solid #e5e7eb;border-radius:8px;padding:16px;display:flex;flex-direction:column}
.card-title{margin:0 0 8px;font-size:1.125rem;line-height:1.4}
.card-desc{margin:0 0 12px;color:#374151}
.card-actions{margin-top:auto;display:flex;flex-wrap:wrap;gap:10px}
.btn{display:inline-block;text-decoration:none;border:1px solid #d1d5db;border-radius:6px;padding:8px 12px}
.btn.primary{border-color:#2563eb}

EOF
base64 -d >"$ROOT/static/docs/oferta.docx" <<'B64EOF'

B64EOF
base64 -d >"$ROOT/static/docs/privacy_policy.docx" <<'B64EOF'

B64EOF
echo "All files written. You can now commit and deploy."